import React, {useState} from 'react'
import { View, StyleSheet, TouchableOpacity, Text, TextInput } from 'react-native'

export const AddNewEx = ({props, navigation}) => {



	const increment = (number) => {
		return number
	}

	const decrement = (number) => {
		return number
	}
	let state = {
		description: '',
		approaches: 0,
		repetitions: 0,
		rest: 0,
	}
	return (
		<View style={styles.wrapper}>
			<View style={styles.descriptionContainer}>

			</View>
			<View style={styles.weightContainer}></View>
			<View style={styles.approachesContainer}>
				<TouchableOpacity
					style={styles.button}
					onPress={increment}
				>
					<Text style={styles.buttonText}>+</Text>
				</TouchableOpacity>

				<TextInput 
                style={styles.textinput}
                onChangeText={(text) => {
                    if (text)
                        state.name = text
                    }}
                placeholder='Кол-во подходов'>
                </TextInput>

				<TouchableOpacity
					style={styles.button}
					onPress={decrement}
				>
					<Text style={styles.buttonText}>-</Text>
				</TouchableOpacity>
			</View>
			<View style={styles.repetitionsContainer}></View>
			<View style={styles.restContainer}></View>
			
		</View>
		
	)
}

const styles = StyleSheet.create({
	wrapper: {
		width: '100%',
		height: '100%',
		flex: 1,
		backgroundColor: '#c4c4c4'
	}
})
